import React, { useState, useEffect } from 'react';
import '../css/DoctorDashboard.css';

const DoctorDashboard = () => {
  const [slots, setSlots] = useState([]);
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [reservationsNum, setReservationsNum] = useState(0);
  const [slotIdToCancel, setSlotIdToCancel] = useState(0);
  const [message, setMessage] = useState('');
  const userId = localStorage.getItem('userId');

  const fetchDoctorSlots = async () => {
    try {
      const response = await fetch(`/api/doctors/slots/${userId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch doctor slots');
      }
      const data = await response.json();
      setSlots(data.slots);
    } catch (error) {
      console.error(error);
      setMessage('Failed to fetch doctor slots');
    }
  };

  useEffect(() => {
    fetchDoctorSlots();
  }, []);

  const addDoctorSlot = async () => {
    try {
      const response = await fetch('/api/doctors/addslot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          doctor_id: userId,
          date,
          start_time: startTime,
          end_time: endTime,
          reservations_num: reservationsNum,
        }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error('Failed to add new slot');
      }
      setSlots(data.slots);
      fetchDoctorSlots();
      setMessage('Slot added successfully');
    } catch (error) {
      setMessage('Failed to add slot');
      console.error(error);
    }
  };

  const cancelDoctorSlot = async () => {
    try {
      const response = await fetch('/api/doctors/cancelslot', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          doctor_id: userId,
          slot_id: slotIdToCancel,
        }),
      });
      if (!response.ok) {
        throw new Error('Failed to cancel slot');
      }
      fetchDoctorSlots();
      setMessage('Slot canceled successfully');
    } catch (error) {
      setMessage('Failed to cancel slot');
      console.error(error);
    }
  };

  return (
    <div className="doctor-dashboard-container">
      <h2>Welcome Doctor</h2>
      <h3>Current Slots</h3>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Date</th>
            <th>Start Time</th>
            <th>End Time</th>
            <th>Reservations Number</th>
          </tr>
        </thead>
        <tbody>
          {slots.map((slot) => (
            <tr key={slot.id}>
              <td>{slot.id}</td>
              <td>{slot.date}</td>
              <td>{slot.start_time}</td>
              <td>{slot.end_time}</td>
              <td>{slot.reservations_num}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="add-slot-container">
        <label>Date:</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <label>Start Time:</label>
        <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
        <label>End Time:</label>
        <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
        <label>Reservations Number:</label>
        <input type="number" value={reservationsNum} onChange={(e) => setReservationsNum(e.target.value)} />
        <button onClick={addDoctorSlot}>Add Slot</button>
      </div>
      <div className="cancel-slot-container">
        <label>Slot ID to Cancel:</label>
        <input type="number" value={slotIdToCancel} onChange={(e) => setSlotIdToCancel(e.target.value)} />
        <button onClick={cancelDoctorSlot}>Cancel Slot</button>
      </div>
      {message && <p className="error-message">{message}</p>}
    </div>
  );
};

export default DoctorDashboard;
