import React, { useState, useEffect } from 'react';

const PatientDashboard = () => {

  const [reservations, setReservations] = useState([]);
  const [slotId, setSlotId] = useState(0);
  const [bookingMessage, setBookingMessage] = useState('');
  const [updatedSlotId, setUpdatedSlotId] = useState(0);
  const [updateMessage, setUpdateMessage] = useState('');

  useEffect(() => {
    const fetchReservations = async () => {
      const patientId = localStorage.getItem('userId'); // Assuming patientId is obtained from local storage
      const response = await fetch(`/api/patients/${patientId}/reservations`);
      const data = await response.json();
      setReservations(data.reservations);
    };
    fetchReservations();
  }, []);

  const handleBooking = async (e) => {
    e.preventDefault();
    const patientId = localStorage.getItem('userId'); // Assuming patientId is obtained from local storage
    const response = await fetch('/api/patients/bookappointment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ patient_id: patientId, slot_id: slotId }),
    });
    const data = await response.json();
    if (response.ok) {
      setBookingMessage('Appointment booked successfully!');
      const updatedReservationsResponse = await fetch(`/api/patients/${patientId}/reservations`);
      const updatedReservationsData = await updatedReservationsResponse.json();
      setReservations(updatedReservationsData.reservations);
    } else {
      setBookingMessage(data.message);
    }
  };

  /*const handleUpdate = async (e, slotId) => {
    e.preventDefault();
    const patientId = localStorage.getItem('userId'); // Assuming patientId is obtained from local storage
    const response = await fetch('/api/patients/updateappointment', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ patient_id: patientId, slot_id: slotId, updated_slot_id: updatedSlotId }),
    });
    const data = await response.json();
    if (response.ok) {
      setUpdateMessage('Appointment updated successfully!');
      const updatedReservationsResponse = await fetch(`/api/patients/${patientId}/reservations`);
      const updatedReservationsData = await updatedReservationsResponse.json();
      setReservations(updatedReservationsData.reservations);
    } else {
      setUpdateMessage(data.message);
    }
  };*/



  return (
    <div>
      <h2>My Appointments</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Appointment</th>
            <th>Doctor</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((reservation) => (
            <tr key={reservation.appointment_id}>
              <td>{reservation.slot_id}</td>
              <td>{formatDate(reservation.date)} - {formatTime(reservation.start_time)} - {formatTime(reservation.end_time)}</td>
              <td>{reservation.doctor_name}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Book an Appointment</h2>
      <form onSubmit={handleBooking}>
        <label>
          Slot ID:
          <input type="number" value={slotId} onChange={(e) => setSlotId(e.target.value)} />
        </label>
        <button type="submit">Book Appointment</button>
      </form>
      <h2>Update Appointment</h2>
      <form onSubmit={handleUpdate}>
        <label>
          Current Slot ID:
          <input type="number" value={slotId} onChange={(e) => setSlotId(e.target.value)} />
        </label>
        <label>
          Updated Slot ID:
          <input type="number" value={updatedSlotId} onChange={(e) => setUpdatedSlotId(e.target.value)} />
        </label>
        <button type="submit">Update Appointment</button>
      </form>
      {updateMessage && <p>{updateMessage}</p>}
    </div>
  );
};

export default PatientDashboard;
