import React, { useState, useEffect } from 'react';

const PatientDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  //const [patientId, setPatientId] = useState(0);
  const [slotId, setSlotId] = useState(0);
  const [message, setMessage] = useState('');
  const userId = localStorage.getItem('userId');

  const fetchAppointments = async () => {
    try {
      const response = await fetch(`/api/patients/${userId}reservations`);
      if (!response.ok) {
        throw new Error('Failed To Fetch Patient Appointments');
      }
      const data = await response.json();
      setAppointments(data.reservations);
    } catch (error) {
      setMessage(error.message);
      console.error(error);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  const bookAppointment = async () => {
    try {
      const response = await fetch('/api/patients/bookappointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          patient_id: userId,
          slot_id: slotId
        })
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message);
      }
      setAppointments(data.reservations);
      fetchAppointments();
      setMessage('Appointment Booked Successfully');
    } catch (error) {
      setMessage(error.message);
      console.error(error);
    }
  };

  return (
    <div className="patient-dashboard-container">
      <h2>Welcome Patient</h2>
      <h3>My Appointments</h3>
      <table>
        <thead>
          <tr>
            <th>Appointment ID</th>
            <th>Doctor Name</th>
            <th>Date</th>
            <th>Start Time</th>
            <th>End Time</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map(appointment => (
          <tr key={appointment.appointment_id}>
            <td>{appointment.appointment_id}</td>
            <td>{appointment.doctor_name}</td>
            <td>{appointment.date}</td>
            <td>{appointment.start_time}</td>
            <td>{appointment.end_time}</td>
        </tr>
      ))}
    </tbody>
  </table>
  <div className="book-appointment-container">
  <label>Slot ID:</label>
    <input type="number" value={slotId} onChange={(e) => setSlotId(e.target.value)} />
    <button onClick={bookAppointment}>Book Appointment</button>
  </div>
  {message && <p className="error-message">{message}</p>}
</div>
  );
};

export default PatientDashboard;
