import React, { useState, useEffect } from 'react';

const PatientDashboard = () => {
  const [reservations, setReservations] = useState([]);
  const [appointmentSlot, setAppointmentSlot] = useState(0); // Initialize appointmentSlot state
  const [message, setMessage] = useState('');
  const userId = localStorage.getItem('userId');

  /*const fetchPatientReservations = async () => {
    try {
      const response = await fetch(`/api/patients/${userId}/reservations`);
      const data = await response.json();
      if (!response.ok) {
        throw new Error('Failed To Fetch Patient Reservations');
      }
      setReservations(data.reservations);
    } catch (error) {
      console.error(error);
      setMessage('Failed To Fetch Patient Reservations');
    } 
  };

  useEffect(() => {
    fetchPatientReservations();
  }, []);*/

  const handleAppointmentSlotChange = (event) => {
    setAppointmentSlot(event.target.value);
  };

  const bookAppointment = async () => {
    try {
      const response = await fetch('/api/patients/bookappointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          patient_id: userId,
          slot_id: appointmentSlot,
        }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message);
      }
      setReservations(data.reservations);
      //fetchPatientReservations();
      setMessage('Appointment Reserved Successfully');
    } catch (error) {
      console.error(error);
      setMessage(error.message);
    }
  };

  return (
    <div>
      <h2>My Appointments</h2>
      <table>
        <thead>
          <tr>
            <th>Appointment</th>
            <th>Doctor</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((reservation) => (
            <tr key={reservation.slot_id.id}>
              <td>
                {`${reservation.slot_id.date} ${reservation.slot_id.start_time} - ${reservation.slot_id.end_time}`}
              </td>
              <td>{reservation.slot_id.doctor_id.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div >
        <label>Appointment Slot:</label>
        <input type="number" value={appointmentSlot} onChange={handleAppointmentSlotChange} />
        <button onClick={bookAppointment}>Reserve Appointment</button>
      </div>
      {message && <p className="error-message">{message}</p>}
    </div>
  );
};

export default PatientDashboard;
